mapper.select:
SELECT ID , box_no, status, ( SELECT u FROM combiner_box_data cbd WHERE cbd.combiner_box_id = cb.ID and device_time >= #{startTime} and device_time <= #{endTime} ORDER BY device_time DESC LIMIT 1 ) u, ( SELECT i FROM combiner_box_data cbd WHERE cbd.combiner_box_id = cb.ID and device_time >= #{startTime} and device_time <= #{endTime} ORDER BY device_time DESC LIMIT 1 ) i, ( SELECT P FROM combiner_box_data cbd WHERE cbd.combiner_box_id = cb.ID and device_time >= #{startTime} and device_time <= #{endTime} ORDER BY device_time DESC LIMIT 1 ) P, ( SELECT temperature FROM combiner_box_data cbd WHERE cbd.combiner_box_id = cb.ID and device_time >= #{startTime} and device_time <= #{endTime} ORDER BY device_time DESC LIMIT 1 ) temperature, ( SELECT COUNT ( 1 ) FROM pv_string WHERE combiner_box_id = cb.ID ) pv_string_count FROM combiner_box cb
and cb.status = #{deviceStatus}
ORDER BY box_no
