mapper.update:
update temperature_data
set device_id = #{deviceId,jdbcType=VARCHAR},
  sensor_index = #{sensorIndex,jdbcType=INTEGER},
  temperature = #{temperature,jdbcType=REAL},
  create_time = #{createTime,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=OTHER}

mapper.select:
SELECT
td.id, td.device_id, td.sensor_index, td.temperature, td.create_time
FROM temperature_data td
INNER JOIN temperature_sensor ts ON td.device_id = ts.collector_device_id AND td.sensor_index = ts.sensor_index

  ts.inverter_id = #{inverterId,jdbcType=OTHER}
  AND ts.pv_string_id = #{pvStringId,jdbcType=OTHER}
  AND td.create_time = (
  SELECT
  "max" ( td.create_time )
  FROM
  temperature_data td
  INNER JOIN temperature_sensor ts ON td.device_id = ts.collector_device_id
  AND td.sensor_index = ts.sensor_index and ts.is_valid=TRUE

    ts.inverter_id = #{inverterId,jdbcType=OTHER}
    AND ts.pv_string_id = #{pvStringId,jdbcType=OTHER}
    AND td.create_time >= #{startTime,jdbcType=TIMESTAMP}
    AND td.create_time <= #{endTime,jdbcType=TIMESTAMP}

  )
